// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title CredentialLedger
 * @dev A simple smart contract to store and verify certificate hashes.
 * It acts as a permanent, immutable ledger.
 */
contract CredentialLedger {
    // The 'owner' of the contract (the SuperAdmin/College)
    address public owner;

    // A mapping to store hashes.
    // It maps a certificate hash (bytes32) to a boolean (true if it exists).
    mapping(bytes32 => bool) public certificates;

    // An event that fires when a new certificate is added.
    // This makes it "watchable".
    event CertificateAdded(bytes32 indexed certHash, address indexed addedBy);

    /**
     * @dev Sets the contract creator as the owner.
     */
    constructor() {
        owner = msg.sender;
    }

    /**
     * @dev A modifier to restrict a function to only the owner.
     */
    modifier onlyOwner() {
        require(msg.sender == owner, "Only the owner can call this function.");
        _;
    }

    /**
     * @dev Adds a new certificate hash to the ledger.
     * Only the owner can do this.
     */
    function addCertificate(bytes32 certHash) public onlyOwner {
        // Check if it already exists
        require(!certificates[certHash], "This certificate hash is already on the ledger.");
        
        // Add it to the mapping
        certificates[certHash] = true;
        
        // Fire the event
        emit CertificateAdded(certHash, msg.sender);
    }

    /**
     * @dev A public function to verify if a hash exists.
     * Anyone can call this.
     */
    function verifyCertificate(bytes32 certHash) public view returns (bool) {
        return certificates[certHash];
    }
}

